    {
        let enc = 0;
        if (this.encumbrance && this.quantity) 
        {
            enc = (this.encumbrance.value * this.quantity.value);
            if (this.encumbrance.value % 1 != 0)
            {
                enc = enc.toFixed(2);
            }
        }
        return enc
    }

